css:
#linter-tooltip {
  margin-top: 3px;
  max-width: 64em;
}
#linter-tooltip linter-message.error {
  color: #a5a7ac;
  background-color: #313235;
  border-left: 8px solid #e06c75;
  border-right: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.error:first-child {
  border-top: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.error:last-child {
  border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.error:last-child::before {
  border-color: transparent transparent #e06c75 #e06c75;
}
#linter-tooltip linter-message.error a {
  color: #a5a7ac;
}
#linter-tooltip linter-message.warning {
  color: #a5a7ac;
  background-color: #313235;
  border-left: 8px solid #d19a66;
  border-right: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.warning:first-child {
  border-top: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.warning:last-child {
  border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.warning:last-child::before {
  border-color: transparent transparent #d19a66 #d19a66;
}
#linter-tooltip linter-message.warning a {
  color: #a5a7ac;
}
#linter-tooltip linter-message.info {
  color: #a5a7ac;
  background-color: #313235;
  border-left: 8px solid #61afef;
  border-right: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.info:first-child {
  border-top: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.info:last-child {
  border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.info:last-child::before {
  border-color: transparent transparent #61afef #61afef;
}
#linter-tooltip linter-message.info a {
  color: #a5a7ac;
}
#linter-tooltip linter-message.info::before {
  opacity: 0;
}
#linter-tooltip linter-message:last-child::before {
  content: "";
  position: absolute;
  top: -4px;
  left: 0;
  border: 4px solid;
}
#linter-tooltip linter-message a {
  font-weight: bold;
}
#linter-tooltip linter-message p {
  margin-bottom: 0;
}
#linter-tooltip linter-message .linter-line {
  display: block;
}
#linter-tooltip linter-message .badge {
  color: #dadbdd;
}
linter-message {
  display: block;
  padding: 2px 5px;
}
.icon.linter-icon::before {
  font-size: 14px;
}
atom-text-editor.editor .linter-row {
  /* Take up the full allowed width */
  left: 0;
  right: 0;
  /* Align the linter dot in the middle */
  display: flex;
  align-items: center;
  justify-content: center;
}
.linter-ui-default-fix-btn {
  color: #61afef;
  background-color: #36373a;
  border-color: #181819;
  border-radius: 3px;
  margin: 0 10px;
  padding: 5px 10px;
  line-height: 100%;
  font-size: 12px;
  vertical-align: middle;
}
.linter-gutter.linter-gutter-info {
  color: #61afef;
  z-index: -1;
}
.linter-gutter.linter-gutter-warning {
  color: #d19a66;
  z-index: 0;
}
.linter-gutter.linter-gutter-error {
  color: #e06c75;
  z-index: 1;
}
atom-text-editor.editor .linter-highlight.linter-info,
.linter-highlight.linter-info {
  background-image: linear-gradient(45deg, transparent 65%, #61afef 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #61afef 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #61afef 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #61afef 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-warning,
.linter-highlight.linter-warning {
  background-image: linear-gradient(45deg, transparent 65%, #d19a66 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #d19a66 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #d19a66 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #d19a66 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-error,
.linter-highlight.linter-error {
  background-image: linear-gradient(45deg, transparent 65%, #e06c75 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #e06c75 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #e06c75 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #e06c75 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view span.name {
  position: relative;
}
.tree-view .linter-info {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #61afef 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #61afef 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #61afef 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #61afef 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-warning {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #d19a66 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #d19a66 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #d19a66 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #d19a66 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-error {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #e06c75 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #e06c75 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #e06c75 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #e06c75 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.linter-gutter.icon::before {
  width: 100%;
  margin-right: 0px;
  font-size: 1em;
}
.linter-status-count a {
  padding: 2px;
}
.linter-status-count a::before {
  font-size: 12px;
}
.linter-status-count.hide-config,
.linter-status-count.hide-pane {
  display: none;
}
.linter-cursor-line {
  width: 100%;
  padding-left: 0;
}
linter-decoration {
  pointer-events: none;
}
